import "./EC2DashboardPage.css";
import React, { useState, useMemo, useEffect } from "react";
import { useTable } from "react-table";
import axios from "axios";

function EC2DashboardPage() {
  const [sortKey, setSortKey] = useState("");
  const [dropdownKey, setDropdownKey] = useState("");
  const [sortedData, setSortedData] = useState([]);
  const [currentPage, setCurrentPage] = useState(1);
  const [showNextButton, setShowNextButton] = useState(false);
  const [sortError, setSortError] = useState("");

  const pageSize = 1000;

  const handleSort = async () => {
    if (!sortKey && !dropdownKey) {
      setSortError("To sort, you must select the attribute to sort by and the order. Please select attribute and order.");
    } else if (!sortKey) {
      setSortError("To sort, you must select the attribute to sort by. Please select attribute.");
    } else if (!dropdownKey) {
      setSortError("To sort, you must select the attribute to sort by and the order. Please select order.");
    } else {
      setSortError("");

      setCurrentPage(1);

      try {
        const response = await axios.post("http://localhost:3001/sortData", { sortKey, dropdownKey });
        setSortedData(response.data);
        setShowNextButton(response.data.length > pageSize);
      } catch (error) {
        console.error("Error sorting data:", error);
      }
    }
  };

  const handleClickNext = () => {
    setCurrentPage((prevPage) => prevPage + 1);
  };

  const data = useMemo(() => {
    const startIndex = (currentPage - 1) * pageSize;
    const endIndex = startIndex + pageSize;
    return sortedData.slice(startIndex, endIndex);
  }, [currentPage, sortedData]);

  const columns = useMemo(
    () => [
      {
        Header: "Name",
        accessor: "name",
      },
      {
        Header: "ID",
        accessor: "id",
      },
      {
        Header: "Type",
        accessor: "type",
      },
      {
        Header: "State",
        accessor: "state",
      },
      {
        Header: "AZ",
        accessor: "az",
      },
      {
        Header: "Public IP",
        accessor: "publicIP",
      },
      {
        Header: "Private IPs",
        accessor: "privateIPs",
      },
    ],
    []
  );

  useEffect(() => {
    setShowNextButton(sortedData.length > pageSize && currentPage * pageSize < sortedData.length);
  }, [sortedData, currentPage]);

  const { getTableProps, getTableBodyProps, headerGroups, rows, prepareRow } = useTable({ columns, data });

  return (
    <div className="App">
      <h1 className="title">Active EC2 Instances</h1>
      <div className="container">
        <div className="transparent-square">
          <div className="dropdown-container">
            <select
              className="attribute-dropdown"
              value={sortKey}
              onChange={(e) => setSortKey(e.target.value)}
            >
              <option value="">Select Attribute</option>
              <option value="name">Name</option>
              <option value="id">ID</option>
              <option value="type">Type</option>
              <option value="state">State</option>
              <option value="az">AZ</option>
              <option value="publicIP">Public IP</option>
              <option value="privateIPs">Private IPs</option>
            </select>
            <select
              className="order-dropdown"
              value={dropdownKey}
              onChange={(e) => setDropdownKey(e.target.value)}
            >
              <option value="">Select Order</option>
              <option value="ascending">Ascending</option>
              <option value="descending">Descending</option>
            </select>
            <button className="sort-button" onClick={handleSort}>
              Sort
            </button>
          </div>
          {sortError && <div className="error-message">{sortError}</div>}
        </div>
        <div className="table-container">
          <div className="table-wrapper">
            <table {...getTableProps()}>
              <thead>
                {headerGroups.map((headerGroup) => (
                  <tr {...headerGroup.getHeaderGroupProps()}>
                    {headerGroup.headers.map((column) => (
                      <th {...column.getHeaderProps()}>{column.render("Header")}</th>
                    ))}
                  </tr>
                ))}
              </thead>
              <tbody {...getTableBodyProps()}>
                {rows.map((row) => {
                  prepareRow(row);
                  return (
                    <tr {...row.getRowProps()}>
                      {row.cells.map((cell) => (
                        <td {...cell.getCellProps()}>{cell.render("Cell")}</td>
                      ))}
                    </tr>
                  );
                })}
              </tbody>
            </table>
          </div>
        </div>
        {showNextButton && (
          <div className="next-button-container">
            <button className="next-button" onClick={handleClickNext}>
              Next
            </button>
          </div>
        )}
      </div>
    </div>
  );
}

export default EC2DashboardPage;
