import "./EC2DashboardPage.css";
import React, { useState, useMemo, useEffect } from "react";
import { useTable } from "react-table";
import axios from "axios";

const Dashboard = () => {
  const location = useLocation();
  const searchParams = new URLSearchParams(location.search);
  const email = searchParams.get("email");}

function EC2DashboardPage() {
  const [sortKey, setSortKey] = useState("");
  const [dropdownKey, setDropdownKey] = useState("");
  const [sortedData, setSortedData] = useState([]);
  const [currentPage, setCurrentPage] = useState(1);
  const [showNextButton, setShowNextButton] = useState(false);
  const [sortError, setSortError] = useState("");
  const [nextButtonClickCount, setNextButtonClickCount] = useState(0);
  const pageSize = 3;
  const [username, setUsername] = useState("email");

  useEffect(() => {
    const token = localStorage.getItem("token");
    if (token) {
      const decodedToken = parseToken(token);
      if (decodedToken) {
        setUsername(decodedToken.username);
      }
    }
  }, []);

  function parseToken(token) {
    try {
      return JSON.parse(atob(token.split(".")[1]));
    } catch (error) {
      console.error("Error decoding token:", error);
      return null;
    }
  }

  const handleSort = async () => {
    if (!sortKey && !dropdownKey) {
      setSortError("To sort, you must select the attribute to sort by and the order. Please select attribute and order.");
    } else if (!sortKey) {
      setSortError("To sort, you must select the attribute to sort by. Please select attribute.");
    } else if (!dropdownKey) {
      setSortError("To sort, you must select the attribute to sort by and the order. Please select order.");
    } else {
      setSortError("");

      setCurrentPage(1);
      setNextButtonClickCount(0);

      try {
        const response = await axios.post("http://localhost:3001/sortData", { sortKey, dropdownKey, startIndex: 0 });
        setSortedData(response.data);
        setShowNextButton(response.data.length >= pageSize);
      } catch (error) {
        console.error("Error sorting data:", error.data);
      }
    }
  };

  const handleClickNext = async () => {
    try {
      const startIndex = pageSize * (nextButtonClickCount + 1);

      let response;

      if (sortKey && dropdownKey) {
        response = await axios.post("http://localhost:3001/sortData", { sortKey, dropdownKey, startIndex });
      } else {
        response = await axios.post("http://localhost:3001/sortData", { startIndex });
      }

      const newData = response.data;

      if (newData.length > 0) {
        setSortedData((prevData) => [...prevData, ...newData]);
        setCurrentPage((prevPage) => prevPage + 1);
        setNextButtonClickCount((prevCount) => prevCount + 1);
        setShowNextButton(newData.length >= pageSize);
      } else {
        setShowNextButton(false);
      }
    } catch (error) {
      console.error("Error fetching data:", error);
    }
  };

  const data = useMemo(() => {
    const startIndex = (currentPage - 1) * pageSize;
    const endIndex = startIndex + pageSize;
    return sortedData.slice(startIndex, endIndex);
  }, [currentPage, sortedData]);

  const columns = useMemo(
    () => [
      {
        Header: "Name",
        accessor: "name",
      },
      {
        Header: "ID",
        accessor: "id",
      },
      {
        Header: "Type",
        accessor: "type",
      },
      {
        Header: "State",
        accessor: "state",
      },
      {
        Header: "AZ",
        accessor: "az",
      },
      {
        Header: "Public IP",
        accessor: "publicIP",
      },
      {
        Header: "Private IPs",
        accessor: "privateIPs",
      },
    ],
    []
  );

  useEffect(() => {
    setShowNextButton(sortedData.length > 0);
  }, [sortedData]);

  return (
    <div className="App">
      <div className="tes">
      <div className="new"> <h1 className="title">Hello, {username} - Active EC2 Instances</h1>
       
        <div className="transparent-square">
          <div className="dropdown-container">
            <select
              className="attribute-dropdown"
              value={sortKey}
              onChange={(e) => setSortKey(e.target.value)}
            >
              <option value="">Select Attribute</option>
              <option value="name">Name</option>
              <option value="id">ID</option>
              <option value="type">Type</option>
              <option value="state">State</option>
              <option value="az">AZ</option>
              <option value="publicIP">Public IP</option>
              <option value="privateIPs">Private IPs</option>
            </select>
            <select
              className="order-dropdown"
              value={dropdownKey}
              onChange={(e) => setDropdownKey(e.target.value)}
            >
              <option value="">Select Order</option>
              <option value="ascending">Ascending</option>
              <option value="descending">Descending</option>
            </select>
            <button className="sort-button" onClick={handleSort}>
              Sort
            </button>
          </div>
          {sortError && <div className="error-message">{sortError}</div>}
        </div>
        <div></div>
        <div className="container">
        <div className="table-container">
          <div className="table-wrapper">
            <table>
              <thead>
                <tr>
                  {columns.map((column) => (
                    <th key={column.accessor}>{column.Header}</th>
                  ))}
                </tr>
              </thead>
              <tbody>
                {data.map((row, index) => (
                  <tr key={index}>
                    {columns.map((column) => (
                      <td key={column.accessor}>{row[column.accessor]}</td>
                    ))}
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        </div>
        </div>
        {showNextButton && (
          <div className="next-button-container">
            <button className="next-button" onClick={handleClickNext}>
              Next
            </button>
          </div>
        )}
     </div>
    </div>
    </div>
  );
}

export default EC2DashboardPage;
