import React, { useState } from "react";

const Register = (props) => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [name, setName] = useState("");
  const [accessKeyId, setAccessKeyId] = useState("");
  const [secretAccessKey, setSecretAccessKey] = useState("");
  const [freeUser, setFreeUser] = useState(true);
  const [registrationMessage, setRegistrationMessage] = useState("");

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const response = await fetch("http://localhost:3001/register", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({
          email,
          password,
          name,
          accessKeyId,
          secretAccessKey,
          freeUser,
        }),
      });

      const data = await response.json();

      if (response.ok) {
        setRegistrationMessage(data.message);
        // Save the registration data and proceed with login
      } else {
        setRegistrationMessage(data.message);
      }
    } catch (error) {
      console.log(error);
      setRegistrationMessage("An error occurred while processing the registration");
    }
  };

  return (
    <div className="auth-form-container">
      <h2>Register</h2>
      <form className="register-form" onSubmit={handleSubmit}>
        <div className="form-group">
          <label htmlFor="name">Full name</label>
          <input
            value={name}
            name="name"
            onChange={(e) => setName(e.target.value)}
            id="name"
            className="form-control"
            placeholder="Full Name"
          />
        </div>
        <div className="form-group">
          <label htmlFor="email">Email</label>
          <input
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            type="email"
            className="form-control"
            placeholder="youremail@gmail.com"
            id="email"
            name="email"
          />
        </div>
        <div className="form-group">
          <label htmlFor="password">Password</label>
          <input
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            type="password"
            className="form-control"
            placeholder="********"
            id="password"
            name="password"
          />
        </div>
        <div className="form-group">
          <label htmlFor="accessKeyId">Access Key ID</label>
          <input
            value={accessKeyId}
            onChange={(e) => setAccessKeyId(e.target.value)}
            type="password"
            className="form-control"
            placeholder="Access Key ID"
            id="accessKeyId"
            name="accessKeyId"
          />
        </div>
        <div className="form-group">
          <label htmlFor="secretAccessKey">Secret Access Key</label>
          <input
            value={secretAccessKey}
            onChange={(e) => setSecretAccessKey(e.target.value)}
            type="password"
            className="form-control"
            placeholder="Secret Access Key"
            id="secretAccessKey"
            name="secretAccessKey"
          />
        </div>
        <div className="form-group">
          <label htmlFor="freeUser">AWS Free User</label>
          <input
            type="checkbox"
            checked={freeUser}
            onChange={(e) => setFreeUser(e.target.checked)}
            id="freeUser"
            name="freeUser"
          />
        </div>
        <div className="form-group">
          <button type="submit" className="btn btn-primary">Submit</button>
        </div>
      </form>
      <button className="link-btn" onClick={() => props.onFormSwitch("login")}>
        Already have an account? Login here.
      </button>
      {registrationMessage && <p className="message">{registrationMessage}</p>}
    </div>
  );
};

export default Register;
