import React, { useState } from "react";
import { Link, Route } from "react-router-dom";
import Login from "./components/Login";
import Register from "./components/Register";
import EC2DashboardPage from "./components/EC2DashboardPage";
import "./App.css";

function App() {
  const [currentForm, setCurrentForm] = useState("login");
  const [email, setEmail] = useState("");

  const toggleForm = (formName, email) => {
    setCurrentForm(formName);
    setEmail(email);
  };

  return (
    <div className="App">
      <nav className="navbar navbar-expand navbar-dark bg-dark">
        <Link to={"/"} className="navbar-brand">
          Umbrella
        </Link>
        <div className="navbar-nav mr-auto">
          <li className="nav-item">
            <Link to={"/home"} className="nav-link">
              Home
            </Link>
          </li>
        </div>
        {currentForm === "ec2-dashboard" && (
          <div className="navbar-nav ml-auto">
            <button className="btn btn-danger" onClick={() => toggleForm("login", "")}>
              Logout
            </button>
          </div>
        )}
      </nav>
      <div className="container mt-3">
        <Route exact path="/" render={() => <Login onFormSwitch={toggleForm} />} />
        <Route path="/home" component={Home} />
        <Route
          path="/ec2-dashboard"
          render={() => <EC2DashboardPage email={email} />}
        />
      </div>
    </div>
  );
}



export default App;

