import "./EC2DashboardPage.css";
import * as React from "react";
import { useTable } from "react-table";
import axios from "axios";

function EC2DashboardPage() {
  const [sortKey, setSortKey] = React.useState("name");
  const [dropdownKey, setDropdownKey] = React.useState("ascending");
  const [sortedData, setSortedData] = React.useState([]);
  const [currentPage, setCurrentPage] = React.useState(1);
  const [showNextButton, setShowNextButton] = React.useState(false);
  const [title, setTitle] = React.useState("");

  React.useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await axios.post("http://localhost:3008/sortData", {
          sortKey,
          dropdownKey,
          currentPage,
        });
        const { sortedData: responseData } = response.data;
        setSortedData(responseData);
        setShowNextButton(showNextButton);
        setTitle(sortedData.length > 0 ? sortedData[0].name : "No Item");
   
      } catch (error) {
        console.log(error);
      }
    };

    fetchData();
  }, [sortKey, dropdownKey, currentPage]); // Run only once on initial component mount

  const handleClickNext = () => {
    setCurrentPage((prevPage) => prevPage + 1);
  };
  

  const data = React.useMemo(() => sortedData, [sortedData]);

  const columns = React.useMemo(
    () => [
      {
        Header: "Name",
        accessor: "name",
      },
      {
        Header: "ID",
        accessor: "id",
      },
      {
        Header: "Type",
        accessor: "type",
      },
      {
        Header: "State",
        accessor: "state",
      },
      {
        Header: "AZ",
        accessor: "az",
      },
      {
        Header: "Public IP",
        accessor: "publicIP",
      },
      {
        Header: "Private IPs",
        accessor: "privateIPs",
      },
    ],
    []
  );

  const { getTableProps, getTableBodyProps, headerGroups, rows, prepareRow } =
    useTable({ columns, data });

  // Sort the data when the sort key or dropdown key changes
  React.useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await axios.post("http://localhost:3008/sortData", {
          sortKey,
          dropdownKey,
          currentPage: 1, // Reset current page to 1 when sorting is triggered
        });
        const { sortedData, showNextButton } = response.data;
        setSortedData(sortedData);
        setShowNextButton(showNextButton);
        setCurrentPage(1);
      } catch (error) {
        console.log(error);
      }
    };

    fetchData();
  }, [sortKey, dropdownKey]);

  return (
    <div className="App">
      <h1 className="title">{title}</h1>
      {/* <h1 className="title">Active EC2 Instances</h1> */}
      <div className="container">
        <div className="transparent-square">
          <div className="dropdown-container">
            <select
              className="attribute-dropdown"
              value={sortKey}
              onChange={(e) => setSortKey(e.target.value)}
            >
              <option value="name">Name</option>
              <option value="id">ID</option>
              <option value="type">Type</option>
              <option value="state">State</option>
              <option value="az">AZ</option>
              <option value="publicIP">Public IP</option>
              <option value="privateIPs">Private IPs</option>
            </select>
            <select
              className="order-dropdown"
              value={dropdownKey}
              onChange={(e) => setDropdownKey(e.target.value)}
            >
              <option value="ascending">Ascending</option>
              <option value="descending">Descending</option>
            </select>
            <button
              className="sort-button"
              onClick={() => setCurrentPage(1)} // Reset current page to 1 when sorting is triggered
            >
              Sort
            </button>
          </div>
        </div>
        <div className="table-container">
          <div className="table-wrapper">
            <table {...getTableProps()}>
              <thead>
                {headerGroups.map((headerGroup) => (
                  <tr {...headerGroup.getHeaderGroupProps()}>
                    {headerGroup.headers.map((column) => (
                      <th {...column.getHeaderProps()}>
                        {column.render("Header")}
                      </th>
                    ))}
                  </tr>
                ))}
              </thead>
              <tbody {...getTableBodyProps()}>
                {rows.map((row) => {
                  prepareRow(row);
                  return (
                    <tr {...row.getRowProps()}>
                      {row.cells.map((cell) => (
                        <td {...cell.getCellProps()}>{cell.render("Cell")}</td>
                      ))}
                    </tr>
                  );
                })}
              </tbody>
            </table>
          </div>
        </div>
        {showNextButton && (
          <div className="next-button-container">
            <button className="next-button" onClick={handleClickNext}>
              Next
            </button>
          </div>
        )}
      </div>
    </div>
  );
}

export default EC2DashboardPage;
