import React, { useState } from "react";
import Login from "./components/Login";
import Register from "./components/Register";
import EC2DashboardPage from "./components/EC2DashboardPage";
import "./App.css";

function App() {
  const [currentForm, setCurrentForm] = useState("login");
  const [email, setEmail] = useState("");

  const toggleForm = (formName, email) => {
    setCurrentForm(formName);
    setEmail(email);
  };

  const renderForm = () => {
    if (currentForm === "login") {
      return <Login onFormSwitch={toggleForm} />;
    } else if (currentForm === "register") {
      return <Register onFormSwitch={toggleForm} />;
    } else if (currentForm === "ec2-dashboard") {
      return <EC2DashboardPage email={email} />;
    }
  };

  return (
    <div className="App">
      <nav className="navbar">
        <div className="navbar-left">
          <span className="navbar-brand">Umbrella</span>
        </div>
        {currentForm === "ec2-dashboard" && (
          <div className="navbar-right">
            <button className="logout-button" onClick={() => toggleForm("login", "")}>
              Logout
            </button>
          </div>
        )}
      </nav>
      {renderForm()}
    </div>
  );
}

export default App;

