import "./EC2Dashboard.css";
import fakeData from "../MOCK_DATA.json";
import * as React from "react";
import { useTable } from "react-table";

function EC2Dashboard() {
  const data = React.useMemo(() => fakeData, []);

  const columns = React.useMemo(
    () => [
      {
        Header: "Name",
        accessor: "name",
      },
      {
        Header: "ID",
        accessor: "id",
      },
      {
        Header: "Type",
        accessor: "type",
      },
      {
        Header: "State",
        accessor: "state",
      },
      {
        Header: "AZ",
        accessor: "az",
      },
      {
        Header: "Public IP",
        accessor: "publicIP",
      },
      {
        Header: "Private IPs",
        accessor: "privateIPs",
      },
    ],
    []
  );

  const { getTableProps, getTableBodyProps, headerGroups, rows, prepareRow } =
    useTable({ columns, data });

  return (
    <div className="App">
      <h1 className="title">Active EC2 Instances</h1>
      <div className="container">
        <div>
          
        </div>
        <h2 className="choose-sorting"></h2>
        <div className="transparent-square">
          <div className="dropdown-container">
            <select className="attribute-dropdown">
              <option value="name">Name</option>
              <option value="id">ID</option>
              <option value="type">Type</option>
              <option value="state">State</option>
              <option value="az">AZ</option>
              <option value="publicIP">Public IP</option>
              <option value="privateIPs">Private IPs</option>
            </select>
            <select className="order-dropdown">
              <option value="ascending">Ascending</option>
              <option value="descending">Descending</option>
            </select>
            {/* Add the "Sort" button */}
            <button className="sort-button">Sort</button>
          </div>
        </div>
        <div className="table-container">
          <div className="table-wrapper">
            <table {...getTableProps()}>
              <thead>
                {headerGroups.map((headerGroup) => (
                  <tr {...headerGroup.getHeaderGroupProps()}>
                    {headerGroup.headers.map((column) => (
                      <th {...column.getHeaderProps()}>
                        {column.render("Header")}
                      </th>
                    ))}
                  </tr>
                ))}
              </thead>
              <tbody {...getTableBodyProps()}>
                {rows.map((row) => {
                  prepareRow(row);
                  return (
                    <tr {...row.getRowProps()}>
                      {row.cells.map((cell) => (
                        <td {...cell.getCellProps()}>
                          {cell.render("Cell")}
                        </td>
                      ))}
                    </tr>
                  );
                })}
              </tbody>
            </table>
          </div>
          {/* Add the "Next" button */}
          <div className="next-button-container">
            <button className="next-button">Next</button>
          </div>
        </div>
      </div>
    </div>
  );
}

export default EC2Dashboard;
