import React, { useState, useMemo } from "react";
import { useLocation } from "react-router-dom";
import axios from "axios";
import { useNavigate } from "react-router-dom";

function EC2DashboardPage({ onLogout }) {
  const location = useLocation();
  const searchParams = new URLSearchParams(location.search);
  const email = searchParams.get("email");
  const navigate = useNavigate();
  const [sortKey, setSortKey] = useState("");
  const [dropdownKey, setDropdownKey] = useState("");
  const [sortedData, setSortedData] = useState([]);
  const [currentPage, setCurrentPage] = useState(1);
  const [showNextButton, setShowNextButton] = useState(true);
  const [sortError, setSortError] = useState("");
  const [region, setSelectedRegion] = useState("");

  const pageSize = 1000;


  const regions = [
    'us-east-1',
    'us-east-2',
    'us-west-1',
    'us-west-2',
    'ap-south-1',
    'ap-southeast-1',
    'ap-southeast-2',
    'ap-northeast-1',
    'ap-northeast-2',
    'ap-northeast-3',
    'ca-central-1',
    'eu-central-1',
    'eu-west-1',
    'eu-west-2',
    'eu-west-3',
    'eu-north-1',
    'sa-east-1',
    'af-south-1',
    'ap-east-1',
    'ap-south-2',
    'ap-southeast-3',
    'ap-southeast-4',
    'eu-south-1',
    'eu-south-2',
    'eu-central-2',
    'me-south-1',
    'me-central-1'
  ];
  const handleSort = async () => {
    
    if (!sortKey && !dropdownKey&&!region) {
      setSortError("To sort, you must select the region you want, attribute to sort by and the order. Please select attribute and order.");
    } else if (!sortKey) {
      setSortError("To sort, you must select the attribute to sort by. Please select attribute.");
    } else if (!dropdownKey) {
      setSortError("To sort, you must select the attribute to sort by and the order. Please select order.");
    } else {
      setSortError("");

      setCurrentPage(1);
      if(sortedData!==[]){
      const sorted = [...sortedData].sort((a, b) => {
        if (dropdownKey === "ascending") {
          return a[sortKey].localeCompare(b[sortKey]);
        } else if (dropdownKey === "descending") {
          return b[sortKey].localeCompare(a[sortKey]);
        }
        setSortedData(sorted);
      })}
      else {
        try {
        let response;

        if (sortKey && dropdownKey) {
          response = await axios.post("http://localhost:3001/getData", { sortKey, dropdownKey, email,region });
        } else {
          setDropdownKey("ascending");
          setSortKey("name");
          setSelectedRegion("us-east-1");
          response = await axios.post("http://localhost:3001/getData", { sortKey, dropdownKey, email,region });
        }
  
        const newData = response.data;
          setSortedData(newData);
          setCurrentPage((prevPage) => prevPage + 1);
         // setShowNextButton(newData.length >= pageSize);
     
      } catch (error) {
        console.error("Error fetching data:", error);
      }
    ;


      }
      ;

     
     // setShowNextButton(sorted.length >= pageSize);
    }
  };
  const handleClickNext = async () => {
    try {
      const startIndex =0;

      let response;

      if (sortKey && dropdownKey) {
        response = await axios.post("http://localhost:3001/sortData", { sortKey, dropdownKey, startIndex, email,region });
      } else {
        setDropdownKey("ascending");
        setSortKey("name");
        setSelectedRegion("us-east-1");
        response = await axios.post("http://localhost:3001/sortData", { sortKey, dropdownKey, startIndex, email,region });
      }

      const newData = response.data;
        setSortedData(newData);
        setCurrentPage((prevPage) => prevPage + 1);
       // setShowNextButton(newData.length >= pageSize);
   
    } catch (error) {
      console.error("Error fetching data:", error);
    }
  };


  const handleLogout = async () => {
    try {
      await axios.post("http://localhost:3001/logout", { email });
      // Clear any necessary state or perform other actions on successful logout
      onLogout(); // Call the onLogout function passed from the App component
      navigate("/login");
      //props.onFormSwitch("login");
    } catch (error) {
      console.error("Error logging out:", error);
    }
  };

  const data = useMemo(() => {
    const startIndex = (currentPage - 1) * pageSize;
    const endIndex = startIndex + pageSize;
    return sortedData.slice(startIndex, endIndex);
  }, [currentPage, sortedData]);

  const columns = useMemo(
    () => [
      {
        Header: "Name",
        accessor: "name",
      },
      {
        Header: "ID",
        accessor: "id",
      },
      {
        Header: "Type",
        accessor: "type",
      },
      {
        Header: "State",
        accessor: "state",
      },
      {
        Header: "AZ",
        accessor: "az",
      },
      {
        Header: "Public IP",
        accessor: "publicIP",
      },
      {
        Header: "Private IPs",
        accessor: "privateIPs",
      },
    ],
    []
  );


  return (
    <div className="App">
          
<div className="logout-button-container">
          <button className="logout-button" onClick={handleLogout}>
            Logout
          </button>
        </div>

      <h1 className="title">Active EC2 Instances </h1>
      <div className="container">
        <div className="transparent-square">
          <div className="dropdown-container">
            <select
              className="attribute-dropdown"
              value={sortKey}
              onChange={(e) => setSortKey(e.target.value)}
            >
              <option value="">Select Attribute</option>
              <option value="name">Name</option>
              <option value="id">ID</option>
              <option value="type">Type</option>
              <option value="state">State</option>
              <option value="az">AZ</option>
              <option value="publicIP">Public IP</option>
              <option value="privateIPs">Private IPs</option>
            </select>
            <select
              className="order-dropdown"
              value={dropdownKey}
              onChange={(e) => setDropdownKey(e.target.value)}
            >
              <option value="">Select Order</option>
              <option value="ascending">Ascending</option>
              <option value="descending">Descending</option>
            </select>
            <select
    className="region-dropdown"
    value={region}
    onChange={(e) => setSelectedRegion(e.target.value)}
  >
    <option value="">Select Region</option>
    {regions.map((region) => (
      <option key={region} value={region}>{region}</option>
    ))}
  </select>
            <button className="sort-button" onClick={handleSort}>
              Sort
            </button>
          </div>
          {sortError && <div className="error-message">{sortError}</div>}
        </div>
        <div className="table-container">
          <div className="table-wrapper">
            <table>
              <thead>
                <tr>
                  {columns.map((column) => (
                    <th key={column.accessor}>{column.Header}</th>
                  ))}
                </tr>
              </thead>
              <tbody>
                {data.map((row, index) => (
                  <tr key={index}>
                    {columns.map((column) => (
                      <td key={column.accessor}>{row[column.accessor]}</td>
                    ))}
                  </tr>
                ))}
              </tbody>
            </table>
          
            </div>
      </div>
      
        {showNextButton && (
          <div className="next-button-container">
            <button className="next-button" onClick={handleClickNext}>
            retrieve EC2 instance for Selected Region
            </button>
            </div>
          
        )} </div>

      </div>
 
  );
}

export default EC2DashboardPage;
