import "./EC2DashboardPage.css";
import fakeData from "../MOCK_DATA.json";
import * as React from "react";
import { useTable } from "react-table";

function EC2DashboardPage() {
  const [currentPage, setCurrentPage] = React.useState(1);
  const [showNextButton, setShowNextButton] = React.useState(fakeData.length > 1000);
  const [sortedData, setSortedData] = React.useState(fakeData);

  const pageSize = 1000;

  const handleClickNext = () => {
    setCurrentPage((prevPage) => prevPage + 1);
  };

  const handleSort = () => {
    setCurrentPage(1);
    setShowNextButton(sortedData.length > pageSize);
    setSortedData((prevData) => {
      // Perform sorting operation based on sortKey and dropdownKey
      const sorted = [...prevData].sort((a, b) => {
        return a.name.localeCompare(b.name);
      });
      return sorted;
    });
  };

  const data = React.useMemo(() => {
    const startIndex = (currentPage - 1) * pageSize;
    const endIndex = startIndex + pageSize;
    return sortedData.slice(startIndex, endIndex);
  }, [currentPage, sortedData]);

  const columns = React.useMemo(
    () => [
      {
        Header: "Name",
        accessor: "name",
      },
      // ...other columns
    ],
    []
  );

  const { getTableProps, getTableBodyProps, headerGroups, rows, prepareRow } =
    useTable({ columns, data });

  React.useEffect(() => {
    setShowNextButton(sortedData.length > pageSize);
  }, [sortedData]);

  return (
    <div className="App">
      <h1 className="title">Active EC2 Instances</h1>
      <div className="container">
        {/* ...other code */}
        {/* Add the "Sort" button */}
        <button className="sort-button" onClick={handleSort}>
          Sort
        </button>
        {/* ...other code */}
        {/* Add the "Next" button */}
        {showNextButton && (
          <div className="next-button-container">
            <button className="next-button" onClick={handleClickNext}>
              Next
            </button>
          </div>
        )}
      </div>
    </div>
  );
}

export default EC2DashboardPage;
