import React, { useState, useMemo } from "react";
import { useLocation } from "react-router-dom";
import axios from "axios";
import { useNavigate } from "react-router-dom";

function HomePage({ onLogout }) {
  const location = useLocation();
  const searchParams = new URLSearchParams(location.search);
  const email = searchParams.get("email");
  const navigate = useNavigate();
  const [file, setFile] = useState(null);
  const [result, setResult] = useState(null);


  const handleLogout = async () => {
    try {
      await axios.post("http://localhost:3001/logout", { email });
      onLogout(); 
      navigate("/login");
    } catch (error) {
      console.error("Error logging out:", error);
    }
  };

  const handleFileChange = (event) => {
    setFile(event.target.files[0]);
  };

  const handleRunModel = async () => {
    if (file) {
      const formData = new FormData();
      formData.append('file', file);

      try {
        const response = await axios.post('http://localhost:3001/run-model', formData, {
          headers: {
            'Content-Type': 'multipart/form-data',
          },
        });
        setResult(response.data); // Assuming the response contains the result
      } catch (error) {
        console.error('Error running model:', error);
      }
    }
  };



  return (
    <div className="App">
      <h1>Detect an Obfuscated Code</h1>

      <div className="file-upload-container">
        <h2>Upload file</h2>
        <input type="file" onChange={handleFileChange} />
      </div>

      <div className="run-model-container">
        <button onClick={handleRunModel}>Run the ML Model with the given file</button>
      </div>

      {result && (
        <div className="result-container">
          <h2>The Result</h2>
          <p>The results are: {result}</p>
        </div>
      )}

      <div className="logout-button-container">
        <button className="logout-button" onClick={handleLogout}>
          Logout
        </button>
      </div>
    </div>
  );
}

export default HomePage;
