import React, { useState } from "react";
import { useNavigate } from "react-router-dom";

const RegisterPage = () => {
  const navigate = useNavigate();
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [error, setError] = useState("");
  const [success, setSuccess] = useState(false);

  const handleRegister = () => {
    // Check if the username already exists in the system (replace with your own logic)
    const isUsernameExists = checkIfUsernameExists(username);

    if (isUsernameExists) {
      setError("This username already exists in the system.");
      setSuccess(false);
    } else {
      // Perform registration logic (replace with your own logic)
      // Assuming registration is successful
      setSuccess(true);
      setError("");
      setUsername("");
      setPassword("");
      navigate("/login"); // Redirect to login page after successful registration
    }
  };

  const checkIfUsernameExists = (username) => {
    // Replace this with your own logic to check if the username already exists
    // You can make an API call or check against an existing list of usernames
  };

  return (
    <div>
      <h2>Register</h2>
      {success && <p>Registration successful! Please login.</p>}
      {error && <p>{error}</p>}
      <form>
        <div>
          <label>Username:</label>
          <input
            type="text"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
          />
        </div>
        <div>
          <label>Password:</label>
          <input
            type="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
          />
        </div>
        <button type="button" onClick={handleRegister}>
          Register
        </button>
      </form>
    </div>
  );
};

export default RegisterPage;
