import "./EC2DashboardPage.css";
import * as React from "react";
import { useTable } from "react-table";

function EC2DashboardPage() {
  const [sortKey, setSortKey] = React.useState(""); // State to store the selected sort key
  const [dropdownKey, setDropdownKey] = React.useState(""); // State to store the selected dropdown key
  const [sortedData, setSortedData] = React.useState([]); // State to store the sorted data
  const [currentPage, setCurrentPage] = React.useState(1); // State to track the current page
  const [showNextButton, setShowNextButton] = React.useState(false); // State to determine if "Next" button should be displayed
  const [sortError, setSortError] = React.useState(""); // State to store the sort error message

  const pageSize = 1000; // Number of items displayed per page

  const handleSort = () => {
    if (!sortKey && !dropdownKey) {
      setSortError("To sort, you must select the attribute to sort by and the order. Please select attribute and order.");
    } else if (!sortKey) {
      setSortError("To sort, you must select the attribute to sort by. Please select attribute.");
    } else if (!dropdownKey) {
      setSortError("To sort, you must select the attribute to sort by and the order. Please select order.");
    } else {
      setSortError(""); // Reset sort error message

      setCurrentPage(1);

      // Send sorting request to the server
      fetch("/sortData", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({
          sortKey,
          dropdownKey,
          currentPage,
        }),
      })
        .then((response) => response.json())
        .then((data) => {
          setSortedData(data.sortedData);
          setShowNextButton(data.showNextButton);
        })
        .catch((error) => {
          console.error("Error:", error);
        });
    }
  };

  const handleClickNext = () => {
    setCurrentPage((prevPage) => prevPage + 1);
  };

  const data = React.useMemo(() => sortedData, [sortedData]);

  const columns = React.useMemo(
    () => [
      {
        Header: "Name",
        accessor: "name",
      },
      {
        Header: "ID",
        accessor: "id",
      },
      {
        Header: "Type",
        accessor: "type",
      },
      {
        Header: "State",
        accessor: "state",
      },
      {
        Header: "AZ",
        accessor: "az",
      },
      {
        Header: "Public IP",
        accessor: "publicIP",
      },
      {
        Header: "Private IPs",
        accessor: "privateIPs",
      },
    ],
    []
  );

  const { getTableProps, getTableBodyProps, headerGroups, rows, prepareRow } = useTable({ columns, data });

  return (
    <div className="App">
      <h1 className="title">Active EC2 Instances</h1>
      <div className="container">
        <div className="transparent-square">
          <div className="dropdown-container">
            <select
              className="attribute-dropdown"
              value={sortKey}
              onChange={(e) => setSortKey(e.target.value)}
            >
              <option value="">Select Attribute</option>
              <option value="name">Name</option>
              <option value="id">ID</option>
              <option value="type">Type</option>
              <option value="state">State</option>
              <option value="az">AZ</option>
              <option value="publicIP">Public IP</option>
              <option value="privateIPs">Private IPs</option>
            </select>
            <select
              className="order-dropdown"
              value={dropdownKey}
              onChange={(e) => setDropdownKey(e.target.value)}
            >
              <option value="">Select Order</option>
              <option value="ascending">Ascending</option>
              <option value="descending">Descending</option>
            </select>
            <button className="sort-button" onClick={handleSort}>
              Sort
            </button>
          </div>
          {sortError && <div className="error-message">{sortError}</div>}
        </div>
        <div className="table-container">
          <div className="table-wrapper">
            <table {...getTableProps()}>
              <thead>
                {headerGroups.map((headerGroup) => (
                  <tr {...headerGroup.getHeaderGroupProps()}>
                    {headerGroup.headers.map((column) => (
                      <th {...column.getHeaderProps()}>{column.render("Header")}</th>
                    ))}
                  </tr>
                ))}
              </thead>
              <tbody {...getTableBodyProps()}>
                {rows.map((row) => {
                  prepareRow(row);
                  return (
                    <tr {...row.getRowProps()}>
                      {row.cells.map((cell) => (
                        <td {...cell.getCellProps()}>{cell.render("Cell")}</td>
                      ))}
                    </tr>
                  );
                })}
              </tbody>
            </table>
          </div>
        </div>
        {showNextButton && (
          <div className="next-button-container">
            <button className="next-button" onClick={handleClickNext}>
              Next
            </button>
          </div>
        )}
      </div>
    </div>
  );
}

export default EC2DashboardPage;
