import React, { useState } from "react";
import { Form } from "react-router-dom";

const Register = (props) => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [name, setName] = useState("");
  const [accessKeyId, setAccessKeyId] = useState("");
  const [secretAccessKey, setSecretAccessKey] = useState("");
  const [freeUser, setFreeUser] = useState(true);
  const [registrationMessage, setRegistrationMessage] = useState("");

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const response = await fetch("http://localhost:3001/register", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({
          email,
          password,
          name,
          accessKeyId,
          secretAccessKey,
          freeUser,
        }),
      });

      const data = await response.json();

      if (response.ok) {
        setRegistrationMessage(data.message);
        // Save the registration data and proceed with login
      } else {
        setRegistrationMessage(data.message);
      }
    } catch (error) {
      console.log(error);
      setRegistrationMessage("An error occurred while processing the registration");
    }
  };

  return (
    <div className="col-md-12">
    <div className="card card-container">
      <img
        src="//ssl.gstatic.com/accounts/ui/avatar_2x.png"
        alt="profile-img"
        className="profile-img-card"
      />
  
    <div className="auth-form-container">
      <h2>Register</h2>
      <div className="register-form" onSubmit={handleSubmit}>
      
        <label htmlFor="name">Username</label>
        <input value={name} name="name" onChange={(e) => setName(e.target.value)} id="name" placeholder="full Name" />
        </div>
        <div> <label htmlFor="email">Email</label>
        <input
          value={email}
          onChange={(e) => setEmail(e.target.value)}
          type="email"
          placeholder="youremail@gmail.com"
          id="email"
          name="email"
        /> </div>
        <div className="register-form">
        <label htmlFor="password">Password</label>
        <input
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          type="password"
          className="form-control"
          placeholder="********"
          id="password"
          name="password"
        /></div>
        <div>
        <label htmlFor="accessKeyId">Access Key ID</label>
        <input
          value={accessKeyId}
          onChange={(e) => setAccessKeyId(e.target.value)}
          type="password"
          placeholder="Access Key ID"
          id="accessKeyId"
          name="accessKeyId"
        />
        </div>
        <div>
        <label htmlFor="secretAccessKey">Secret Access Key</label>
        <input
          value={secretAccessKey}
          onChange={(e) => setSecretAccessKey(e.target.value)}
          type="password"
          placeholder="Secret Access Key"
          id="secretAccessKey"
          name="secretAccessKey"
        />
        </div>
        <div>
        <label htmlFor="free User">Free User</label>
        <input
          type="checkbox"
          checked={freeUser}
          onChange={(e) => setFreeUser(e.target.checked)}
          id="freeUser"
          name="freeUser"
        />
        </div><div>
        <button type="submit">Submit</button>
        </div>
      <button className="link-btn" onClick={() => props.onFormSwitch("login")}>
        Already have an account? Login here.
      </button>
      {registrationMessage && <p className="message">{registrationMessage}</p>}
    </div>
    </div>
    </div>
  );
};

export default Register;
